	/* bottom of stack is cache-aligned for sse use */
.equ		_ix,		0
.equ		_iy,		16
.equ		_iz,		32
.equ		_iq,		48
.equ		_tsc,		64
.equ		_qq,		80	
.equ		_c6,		96
.equ		_c12,		112
.equ		_vctot,		128
.equ		_vnbtot,	144
.equ		_half,		160
.equ		_three,		176
.equ		_is3,		192
.equ		_ii3,		196
.equ		_ntia,		200	
