	mulpd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulpd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulpd xmm1, xmm5	
