	movapd xmm4, xmm6
	mulpd  xmm4, xmm4	/* xmm4=rinvtwelve */
	mulpd  xmm6, [esp + _c6]
	mulpd  xmm4, [esp + _c12]
	movapd xmm7, [esp + _vnbtot]
	addpd  xmm7, xmm4
	subpd  xmm7, xmm6
	movapd [esp + _vnbtot], xmm7
		
