.imc3320_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   [ebp + _shift],  4  /* advance pointer one step */
	
	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

	movapd xmm3, xmm0
	movapd xmm4, xmm1
	movapd xmm5, xmm2

	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 
	mov   [esp + _ii3], ebx

	addsd xmm3, [eax + ebx*8]
	addsd xmm4, [eax + ebx*8 + 8]
	addsd xmm5, [eax + ebx*8 + 16]		
	shufpd xmm3, xmm3, 0
	shufpd xmm4, xmm4, 0
	shufpd xmm5, xmm5, 0
	movapd [esp + _ixO], xmm3
	movapd [esp + _iyO], xmm4
	movapd [esp + _izO], xmm5

	movsd xmm3, xmm0
	movsd xmm4, xmm1
	movsd xmm5, xmm2
	addsd xmm0, [eax + ebx*8 + 24]
	addsd xmm1, [eax + ebx*8 + 32]
	addsd xmm2, [eax + ebx*8 + 40]		
	addsd xmm3, [eax + ebx*8 + 48]
	addsd xmm4, [eax + ebx*8 + 56]
	addsd xmm5, [eax + ebx*8 + 64]		

	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0
	shufpd xmm3, xmm3, 0
	shufpd xmm4, xmm4, 0
	shufpd xmm5, xmm5, 0
	movapd [esp + _ixH1], xmm0
	movapd [esp + _iyH1], xmm1
	movapd [esp + _izH1], xmm2
	movapd [esp + _ixH2], xmm3
	movapd [esp + _iyH2], xmm4
	movapd [esp + _izH2], xmm5
	
	/* clear vctot */
	xorpd xmm4, xmm4
	movapd [esp + _vctot], xmm4
	movapd [esp + _vnbtot], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   .imc3320_unroll_loop
	jmp   .imc3320_checksingle
.imc3320_unroll_loop:
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]
	
	add   [esp + _innerjjnr],  8 /* advance pointer (unrolled 2) */

	mov esi, [ebp + _charge]        /* base of charge[] */
	
	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]
	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	movd  mm0, eax		/* use mmx registers as temp storage */
	movd  mm1, ebx
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov ebx, [esi + ebx*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1	
	shl ebx, 1	
	mov edi, [esp + _ntia]
	add eax, edi
	add ebx, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	movupd xmm7, [esi + ebx*8]	/* c6b c12b */
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movd  ebx, mm1
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6

	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]		

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm6, xmm5
	addpd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2

	/* square it */
	mulpd xmm3,xmm3
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	addpd xmm5, xmm4
	addpd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtpd2ps xmm2, xmm7	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm7	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulpd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtpd2ps xmm2, xmm6	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm6	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulpd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtpd2ps xmm2, xmm5	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm5	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulpd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	/* rO is still in xmm7 */
	movapd xmm0, [esp + _rinvO]
	mulpd   xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	movd mm0, eax	
	movd mm1, ebx
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now) */
	lea   ebx, [ebx + ebx*2]
	
	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 
        mulpd  xmm6, xmm1       /* xmm6=Geps */
        mulpd  xmm7, xmm2       /* xmm7=Heps2 */
        addpd  xmm5, xmm6
        addpd  xmm5, xmm7       /* xmm5=Fp */       
        movapd xmm0, [esp + _qqO]
        mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
        addpd  xmm5, xmm4 /* xmm5=VV */
        mulpd  xmm5, xmm0 /* vcoul=qq*VV */ 
	/* at this point xmm5 contains vcoul */
	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addpd  xmm5, [esp + _vctot]
	movapd [esp + _vctot], xmm5 
	
	/* Dispersion */
	movupd xmm4, [esi + eax*8 + 32]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8 + 32]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 48]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 48]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* Dispersion table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */

	mulpd  xmm5, [esp + _c6]	 /* vnb6 */
		
	addpd  xmm5, [esp + _vnbtot]
	movapd [esp + _vnbtot], xmm5
	
	/* Repulsion */
	movupd xmm4, [esi + eax*8 + 64]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8 + 64]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 80]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 80]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* Dispersion table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */

	mulpd  xmm5, [esp + _c12] /* vnb12 */
	addpd  xmm5, [esp + _vnbtot]
	movapd [esp + _vnbtot], xmm5

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulpd xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now) */	
	lea   ebx, [ebx + ebx*2]
	
	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul*/
        /* increment vcoul */
        addpd  xmm5, [esp + _vctot]
        movapd [esp + _vctot], xmm5 

	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulpd   xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now)*/
	lea   ebx, [ebx + ebx*2]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addpd  xmm5, [esp + _vctot]
        movapd [esp + _vctot], xmm5 
			
	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    .imc3320_checksingle
	jmp   .imc3320_unroll_loop
.imc3320_checksingle:	
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz   .imc3320_dosingle
	jmp   .imc3320_updateouterdata
.imc3320_dosingle:
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]

	mov esi, [ebp + _charge]        /* base of charge[] */
	xorpd xmm3, xmm3	
	movlpd xmm3, [esi + eax*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]

	movd  mm0, eax		/* use mmx registers as temp storage */
	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	
	
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1	
	mov edi, [esp + _ntia]
	add eax, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */
	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	
	/* move coords to xmm0-xmm2 */
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm6, xmm5
	addsd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2

	/* square it */
	mulsd xmm3,xmm3
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	addsd xmm5, xmm4
	addsd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtsd2ss xmm2, xmm7	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm7	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulsd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */

	/* rsqH1 - seed in xmm2 */
	cvtsd2ss xmm2, xmm6	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm6	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulsd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtsd2ss xmm2, xmm5	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm5	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulsd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	movd mm0, eax	
	/* rO is still in xmm7 */
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm7
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	 	
	shl eax, 2		
	mov  esi, [ebp + _VFtab]
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now) */	
	
	movupd xmm4, [esi + eax*8]	/* Y1 F1 */
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 
        mulsd  xmm6, xmm1       /* xmm6=Geps */
        mulsd  xmm7, xmm2       /* xmm7=Heps2 */
        addsd  xmm5, xmm6	/* F+Geps */
        addsd  xmm5, xmm7       /* xmm5=Fp=F+Geps+Heps2 */       
        mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
        addsd  xmm5, xmm4 /* xmm5=VV */
	
        movapd  xmm0, [esp + _qqO]
        mulsd  xmm5, xmm0 /* vcoul=qq*VV */ 
	/* at this point xmm5 contains vcoul */
	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addsd  xmm5, [esp + _vctot]
	movlpd [esp + _vctot], xmm5 
	
	/* Dispersion */
	movupd xmm4, [esi + eax*8 + 32]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 48]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* Dispersion table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */

	mulsd  xmm5, [esp + _c6]	 /* vnb6 */
		
	addsd  xmm5, [esp + _vnbtot]
	movsd [esp + _vnbtot], xmm5
	
	/* Repulsion */
	movupd xmm4, [esi + eax*8 + 64]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 80]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* Dispersion table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */

	mulsd  xmm5, [esp + _c12] /* vnb12 */

	addsd  xmm5, [esp + _vnbtot]
	movsd [esp + _vnbtot], xmm5

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulpd xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now)*/	

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 

	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	lea   eax, [eax + eax*2] /* idx *= 3 (total *=12 now)*/	

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 

.imc3320_updateouterdata:
	/* get gid */
	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
        
	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .imc3320_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .imc3320_outer
.imc3320_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 440
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret


	
	
