.section .text
.align 16

sse2_minushalf:	
        .long 0x00000000
	.long 0xbfe00000	
	.long 0000000000
	.long 0xbfe00000	
sse2_half:	
        .long 0x00000000
	.long 0x3fe00000
	.long 0x00000000
	.long 0x3fe00000
sse2_two:	
        .long 0x00000000
	.long 0x40000000
	.long 0x00000000
	.long 0x40000000
sse2_three:	
        .long 0x00000000
	.long 0x40080000
	.long 0x00000000
	.long 0x40080000
sse2_six:
        .long 0x00000000
	.long 0x40180000
	.long 0x00000000
	.long 0x40180000
sse2_twelve:	
        .long 0x00000000
	.long 0x40280000
	.long 0x00000000
	.long 0x40280000


.globl checksse2	 ;# try to issue a SSE2 instruction 
.globl _checksse2
checksse2:
_checksse2:
	emms
	xorpd xmm0,xmm0
	emms
	ret

.align 16
	
.globl vecinvsqrt_sse2
.globl _vecinvsqrt_sse2
vecinvsqrt_sse2:	
_vecinvsqrt_sse2:	
	push ebp
	mov ebp,esp	
	push eax
	push ebx
	push ecx
	push edx

	mov eax, [ebp + 8]
	mov ebx, [ebp + 12]	
	mov ecx, [ebp + 16]
    mov edx, ecx
	movapd xmm6,[sse2_three]
	movapd xmm7,[sse2_minushalf]
    shr ecx, 2
	jnz .vecinvsqrt_loop4
	jmp .vecinvsqrt_iter2
    emms	
.vecinvsqrt_loop4:	
	movapd xmm0,[eax]	
	add eax,  16
	cvtpd2ps xmm4, xmm0
	movapd xmm2,[eax]
	add eax,  16
	cvtpd2ps xmm5, xmm2
	rsqrtps xmm1, xmm4	
	rsqrtps xmm3, xmm5
	movapd  xmm4, xmm0	
	movapd  xmm5, xmm2	
	cvtps2pd xmm1, xmm1	
	cvtps2pd xmm3, xmm3	
	mulpd xmm0,xmm1		
    mulpd xmm2,xmm3		
	mulpd xmm0,xmm1		
    mulpd xmm2,xmm3		
	subpd xmm0,xmm6		
	subpd xmm2,xmm6		
	mulpd xmm0,xmm1		
	mulpd xmm2,xmm3
	mulpd xmm0,xmm7		
	mulpd xmm2,xmm7		
	mulpd xmm4,xmm0		
    mulpd xmm5,xmm2		
	mulpd xmm4,xmm0		
    mulpd xmm5,xmm2		
	subpd xmm4,xmm6		
	subpd xmm5,xmm6		
	mulpd xmm4,xmm0		
	mulpd xmm5,xmm2
	mulpd xmm4,xmm7		
	mulpd xmm5,xmm7		
	movapd [ebx],xmm4
	add ebx,  16
	movapd [ebx],xmm5
	add ebx,  16
        dec ecx
    jecxz .vecinvsqrt_iter2
    jmp .vecinvsqrt_loop4
.vecinvsqrt_iter2:
    mov ecx,edx
    and ecx, 2
    jecxz .vecinvsqrt_iter1
	movapd xmm0,[eax]
	add eax,  16
	cvtpd2ps xmm4, xmm0
	rsqrtps xmm1, xmm4
	movapd xmm4, xmm0
	cvtps2pd xmm1, xmm1	
	mulpd xmm0,xmm1
	mulpd xmm0,xmm1		
	subpd xmm0,xmm6		
	mulpd xmm0,xmm1		
	mulpd xmm0,xmm7		
	mulpd xmm4,xmm0		
	mulpd xmm4,xmm0		
	subpd xmm4,xmm6		
	mulpd xmm4,xmm0		
	mulpd xmm4,xmm7			
	movapd [ebx],xmm4
	add ebx,  16        
.vecinvsqrt_iter1:
    mov ecx,edx
    and ecx, 1
    jecxz .vecinvsqrt_end
	movsd xmm0,[eax]
	add eax,  8
	cvtsd2ss xmm4, xmm0
	rsqrtss xmm1, xmm4	
	movsd xmm4, xmm0
	cvtss2sd xmm1, xmm1	
	mulsd xmm0,xmm1
	mulsd xmm0,xmm1		
	subsd xmm0,xmm6		
	mulsd xmm0,xmm1		
	mulsd xmm0,xmm7		
	mulsd xmm4,xmm0		
	mulsd xmm4,xmm0		
	subsd xmm4,xmm6		
	mulsd xmm4,xmm0		
	mulsd xmm4,xmm7			
	movsd [ebx],xmm4
	add ebx,  8
.vecinvsqrt_end:	
	emms
	pop edx
	pop ecx
	pop ebx
	pop eax
	leave
	ret
	
	
.globl vecrecip_sse2
.globl _vecrecip_sse2
vecrecip_sse2:	
_vecrecip_sse2:	
	push ebp
	mov ebp,esp	
	push eax
	push ebx
	push ecx
	push edx

	mov eax, [ebp + 8]
	mov ebx, [ebp + 12]	
	mov ecx, [ebp + 16]
    mov edx, ecx
	movapd xmm6,[sse2_two]
    shr ecx, 2
	jnz .vecrecip_loop4
    jmp .vecrecip_iter2
    emms	
.vecrecip_loop4:	
	movapd xmm0,[eax]	;# xa 
	add eax,  16
	cvtpd2ps xmm4, xmm0	
	movapd xmm2,[eax]	;# xb 
	add eax,  16
	cvtpd2ps xmm5, xmm2
	rcpps xmm4, xmm4	
	rcpps xmm5, xmm5
	movapd xmm7, xmm6
	movapd xmm1, xmm0	;# xa 
	movapd xmm3, xmm2	;# xb 
	cvtps2pd xmm4, xmm4	;# lu a 	
	cvtps2pd xmm5, xmm5	;# lu b 
	mulpd xmm0, xmm4	;# x*lu a 
	mulpd xmm2, xmm5	;# x*lu b 
	subpd xmm7, xmm0	;# 2-x*lu a 
	mulpd xmm4, xmm7	;# lu*(2-x*lu) a 
	movapd xmm7, xmm6	;# 2.0 
	subpd xmm7, xmm2	;# 2-x*lu b 
	mulpd xmm5, xmm7	;# lu*(2-x*lu) b 
	movapd xmm7, xmm6	
	mulpd xmm1, xmm4	;# x*lu a 
	mulpd xmm3, xmm5	;# x*lu b 
	subpd xmm7, xmm1	;# 2-x*lu a 
	mulpd xmm4, xmm7	;# lu*(2-x*lu) a 
	movapd xmm7, xmm6	;# 2.0 
	subpd xmm7, xmm3	;# 2-x*lu b 
	mulpd xmm5, xmm7	;# lu*(2-x*lu) b 	
	movapd [ebx],xmm4
	add ebx,  16
	movapd [ebx],xmm5
	add ebx,  16
        dec ecx
    jecxz .vecrecip_iter2
    jmp .vecrecip_loop4
.vecrecip_iter2:
    mov ecx,edx
    and ecx, 2
    jecxz .vecrecip_iter1
	movapd xmm0,[eax]	
	add eax,  16
	cvtpd2ps xmm4, xmm0	
	rcpps xmm4, xmm4
	cvtps2pd xmm4, xmm4	;# lu 
	movapd xmm3, xmm6	;# 2 
	movapd xmm1,xmm0	;# x 
	mulpd xmm0,xmm4		;# lu*x 
	subpd xmm3,xmm0		;# 2-lu*x 
	mulpd xmm4, xmm3	;# iter1: lu=lu*(2-lu*x) 

	movapd xmm3, xmm6
	mulpd xmm1, xmm4	;# x*lu 
	subpd xmm3, xmm1	;# 2-x*lu 
	mulpd xmm4, xmm3	;# 1/x 
	movapd [ebx],xmm4
	add ebx,  16        
.vecrecip_iter1:
    mov ecx,edx
    and ecx, 1
    jecxz .vecrecip_end
	movsd xmm0,[eax]
	cvtsd2ss xmm4, xmm0	
	rcpss xmm4, xmm4
	cvtss2sd xmm4, xmm4	;# lu 
	movapd xmm3, xmm6	;# 2 
	movsd xmm1,xmm0		;# x 
	mulsd xmm0,xmm4		;# lu*x 
	subsd xmm3,xmm0		;# 2-lu*x 
	mulsd xmm4, xmm3	;# iter1: lu=lu*(2-lu*x) 

	mulsd xmm1, xmm4	;# x*lu 
	subsd xmm6, xmm1	;# 2-x*lu 
	mulsd xmm4, xmm6	;# 1/x 
	movsd [ebx],xmm4
.vecrecip_end:	
	emms
	pop edx
	pop ecx
	pop ebx
	pop eax
	leave
	ret
	
	
.globl inl0100_sse2
.globl _inl0100_sse2
inl0100_sse2:	
_inl0100_sse2:	
.equiv		i0100_nri, 			8
.equiv		i0100_iinr,			12
.equiv		i0100_jindex,		16
.equiv		i0100_jjnr,			20
.equiv		i0100_shift,		24
.equiv		i0100_shiftvec,		28
.equiv		i0100_fshift,		32
.equiv		i0100_gid,			36
.equiv		i0100_pos,			40		
.equiv		i0100_faction,		44
.equiv		i0100_type,			48
.equiv		i0100_ntype,		52
.equiv		i0100_nbfp,			56	
.equiv		i0100_Vnb,			60	
	;# stack offsets for local variables  
	;# bottom of stack is cache-aligned for sse use 
.equiv		i0100_ix,			0
.equiv		i0100_iy,	        16
.equiv		i0100_iz,           32
.equiv		i0100_dx,           48
.equiv		i0100_dy,           64
.equiv		i0100_dz,           80
.equiv		i0100_two,          96		
.equiv		i0100_c6,			112
.equiv		i0100_c12,			128
.equiv		i0100_six,			144
.equiv		i0100_twelve,		160		 
.equiv		i0100_vnbtot,		176
.equiv		i0100_fix,			192
.equiv		i0100_fiy,			208
.equiv		i0100_fiz,			224
.equiv		i0100_half,			240
.equiv		i0100_three,		256
.equiv		i0100_is3,			272
.equiv		i0100_ii3,			276
.equiv		i0100_ntia,			280	
.equiv		i0100_innerjjnr,    284
.equiv		i0100_innerk,       288
.equiv		i0100_salign,       292
	push ebp
	mov ebp,esp	
    push eax
    push ebx
    push ecx
    push edx
	push esi
	push edi
	sub esp, 296		;# local stack space 
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + i0100_salign], eax

	emms

	movapd xmm1, [sse2_two]
	movapd xmm2, [sse2_six]
	movapd xmm3, [sse2_twelve]
	movapd [esp + i0100_two], xmm1
	movapd [esp + i0100_six],  xmm2
	movapd [esp + i0100_twelve], xmm3

	;# assume we have at least one i particle - start directly 	
.i0100_outer:
	mov   eax, [ebp + i0100_shift]      ;# eax = pointer into shift[] 
	mov   ebx, [eax]		;# ebx=shift[n] 
	add   dword ptr [ebp + i0100_shift],  4  ;# advance pointer one step 

	lea   ebx, [ebx + ebx*2]    ;# ebx=3*is 
	mov   [esp + i0100_is3],ebx    	;# store is3 

	mov   eax, [ebp + i0100_shiftvec]   ;# eax = base of shiftvec[] 

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + i0100_iinr]       ;# ecx = pointer into iinr[] 	
	add   dword ptr [ebp + i0100_iinr],  4   ;# advance pointer 
	mov   ebx, [ecx]	    ;# ebx =ii 

    mov   edx, [ebp + i0100_type] 
    mov   edx, [edx + ebx*4]
    imul  edx, [ebp + i0100_ntype]
    shl   edx, 1
    mov   [esp + i0100_ntia], edx
		
	lea   ebx, [ebx + ebx*2]	;# ebx = 3*ii=ii3 
	mov   eax, [ebp + i0100_pos]    ;# eax = base of pos[]  

	addsd xmm0, [eax + ebx*8]
	addsd xmm1, [eax + ebx*8 + 8]
	addsd xmm2, [eax + ebx*8 + 16]
	
	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + i0100_ix], xmm0
	movapd [esp + i0100_iy], xmm1
	movapd [esp + i0100_iz], xmm2

	mov   [esp + i0100_ii3], ebx
	
	;# clear vnbtot and i forces 
	xorpd xmm4, xmm4
	movapd [esp + i0100_vnbtot], xmm4
	movapd [esp + i0100_fix], xmm4
	movapd [esp + i0100_fiy], xmm4
	movapd [esp + i0100_fiz], xmm4
	
	mov   eax, [ebp + i0100_jindex]
	mov   ecx, [eax]	     ;# jindex[n] 
	mov   edx, [eax + 4]	     ;# jindex[n+1] 
	add   dword ptr [ebp + i0100_jindex],  4
	sub   edx, ecx               ;# number of innerloop atoms 

	mov   esi, [ebp + i0100_pos]
	mov   edi, [ebp + i0100_faction]	
	mov   eax, [ebp + i0100_jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + i0100_innerjjnr], eax     ;# pointer to jjnr[nj0] 
	sub   edx,  2
	mov   [esp + i0100_innerk], edx    ;# number of innerloop atoms 
	
	jge   .i0100_unroll_loop
	jmp   .i0100_checksingle
.i0100_unroll_loop:
	;# twice unrolled innerloop here 
	mov   edx, [esp + i0100_innerjjnr]     ;# pointer to jjnr[k] 
	mov   eax, [edx]	
	mov   ebx, [edx + 4]              
	add   dword ptr [esp + i0100_innerjjnr],  8 ;# advance pointer (unrolled 2) 

	movd  mm0, eax		;# use mmx registers as temp storage 
	movd  mm1, ebx
	
	mov esi, [ebp + i0100_type]
	mov eax, [esi + eax*4]
	mov ebx, [esi + ebx*4]
	mov esi, [ebp + i0100_nbfp]
	shl eax, 1
	shl ebx, 1
	mov edi, [esp + i0100_ntia]
	add eax, edi
	add ebx, edi

	movlpd xmm6, [esi + eax*8]
	movlpd xmm7, [esi + ebx*8]
	movhpd xmm6, [esi + eax*8 + 8]	;# c6a c12a 
	movhpd xmm7, [esi + ebx*8 + 8]	;# c6b c12b 
	
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7

	movd  eax, mm0		
	movd  ebx, mm1	
	
	movapd [esp + i0100_c6], xmm4
	movapd [esp + i0100_c12], xmm6

	mov esi, [ebp + i0100_pos]       ;# base of pos[] 

	lea   eax, [eax + eax*2]     ;# replace jnr with j3 
	lea   ebx, [ebx + ebx*2]	

	;# move two coordinates to xmm0-xmm2 	

	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]

	;# move ix-iz to xmm4-xmm6 
	movapd xmm4, [esp + i0100_ix]
	movapd xmm5, [esp + i0100_iy]
	movapd xmm6, [esp + i0100_iz]

	;# calc dr 
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	;# store dr 
	movapd [esp + i0100_dx], xmm4 
	movapd [esp + i0100_dy], xmm5
	movapd [esp + i0100_dz], xmm6
	;# square it 
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6	;# rsq in xmm4 

	cvtpd2ps xmm6, xmm4	
	rcpps xmm6, xmm6
	cvtps2pd xmm6, xmm6	;# lu in low xmm6 
	
	;# 1/x lookup seed in xmm6 
	movapd xmm0, [esp + i0100_two]
	movapd xmm5, xmm4
	mulpd xmm4, xmm6	;# lu*rsq 
	subpd xmm0, xmm4	;# 2-lu*rsq 
	mulpd xmm6, xmm0	;# (new lu) 
	
	movapd xmm0, [esp + i0100_two]
	mulpd xmm5, xmm6	;# lu*rsq 
	subpd xmm0, xmm5	;# 2-lu*rsq 
	mulpd xmm0, xmm6	;# xmm0=rinvsq 

	movapd xmm1, xmm0
	mulpd  xmm1, xmm0
	mulpd  xmm1, xmm0	;# xmm1=rinvsix 
	movapd xmm2, xmm1
	mulpd  xmm2, xmm2	;# xmm2=rinvtwelve 

	mulpd  xmm1, [esp + i0100_c6]
	mulpd  xmm2, [esp + i0100_c12]
	movapd xmm5, xmm2
	subpd  xmm5, xmm1	;# vnb=vnb12-vnb6 
	addpd  xmm5, [esp + i0100_vnbtot]
	mulpd  xmm1, [esp + i0100_six]
	mulpd  xmm2, [esp + i0100_twelve]
	subpd  xmm2, xmm1
	mulpd  xmm0, xmm2	;# xmm4=total fscal 
	movapd xmm4, xmm0
	
	movapd xmm0, [esp + i0100_dx]
	movapd xmm1, [esp + i0100_dy]
	movapd xmm2, [esp + i0100_dz]

	movapd [esp + i0100_vnbtot], xmm5

	mov    edi, [ebp + i0100_faction]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	;# xmm0-xmm2 contains tx-tz (partial force) 
	;# now update f_i 
	movapd xmm3, [esp + i0100_fix]
	movapd xmm4, [esp + i0100_fiy]
	movapd xmm5, [esp + i0100_fiz]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm5, xmm2
	movapd [esp + i0100_fix], xmm3
	movapd [esp + i0100_fiy], xmm4
	movapd [esp + i0100_fiz], xmm5
	
	;# the fj's - start by accumulating forces from memory 
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
		
	;# should we do one more iteration? 
	sub   dword ptr [esp + i0100_innerk],  2
	jl    .i0100_checksingle
	jmp   .i0100_unroll_loop
.i0100_checksingle:				
	mov   edx, [esp + i0100_innerk]
	and   edx, 1
	jnz    .i0100_dosingle
	jmp    .i0100_updateouterdata
.i0100_dosingle:
	mov edi, [ebp + i0100_pos]
	mov   ecx, [esp + i0100_innerjjnr]
	mov   eax, [ecx]		

	movd  mm0, eax		;# use mmx registers as temp storage 	
	mov esi, [ebp + i0100_type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + i0100_nbfp]
	shl eax, 1
	mov edi, [esp + i0100_ntia]
	add eax, edi

	movlpd xmm6, [esi + eax*8]
	movhpd xmm6, [esi + eax*8 + 8]	;# c6a c12a 

		xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7

	movd  eax, mm0		
	
	movapd [esp + i0100_c6], xmm4
	movapd [esp + i0100_c12], xmm6
	
	mov esi, [ebp + i0100_pos]       ;# base of pos[] 

	lea   eax, [eax + eax*2]     ;# replace jnr with j3 

	;# move coordinates to xmm0-xmm2 	

	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	
	;# move ix-iz to xmm4-xmm6 
	movapd xmm4, [esp + i0100_ix]
