	movapd xmm0, [esp + i2100_fix]
	movapd xmm1, [esp + i2100_fiy]
	movapd xmm2, [esp + i2100_fiz]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
