    addsd  xmm5, [esp + i3020_vctot]
    movlpd [esp + i3020_vctot], xmm5 
	xorpd  xmm4, xmm4

	mulsd  xmm3, [esp + i3020_tsc]
	mulsd  xmm3, [esp + i3020_rinvO]	
