	addsd  xmm0, [esp + i3020_fjx]
	addsd  xmm1, [esp + i3020_fjy]
	addsd  xmm2, [esp + i3020_fjz]
	movlpd [esp + i3020_fjx], xmm0
	movlpd [esp + i3020_fjy], xmm1
	movlpd [esp + i3020_fjz], xmm2

