	mov   ecx, [ebp + i3030_nri]
	dec ecx
	jecxz .i3030_end
	;# not last, iterate once more!  
	mov [ebp + i3030_nri], ecx
	jmp .i3030_outer
.i3030_end:
	emms
	mov eax, [esp + i3030_salign]
	add esp, eax
	add esp, 1444
	pop edi
	pop esi
    pop edx
    pop ecx
    pop ebx
    pop eax
	leave
	ret
	



