	add dword ptr [esp + mci1120_innerjjnr],  8	;# advance pointer (unrolled 2) 

	mov esi, [ebp + mci1120_charge]    ;# base of charge[] 
	
	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]
	movapd xmm4, xmm3
	mulpd  xmm3, [esp + mci1120_iqO]
	mulpd  xmm4, [esp + mci1120_iqH]

