	;# accumulate 
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	;# low xmm7 has the sum now 

	;# add earlier value from mem 
	mov   eax, [ebp + mci1130_Vc]
	addsd xmm7, [eax + edx*8] 
	;# move back to mem 
	movsd [eax + edx*8], xmm7 
	
	;# accumulate total lj energy and update it 
	movapd xmm7, [esp + mci1130_vnbtot]
	;# accumulate 
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	;# low xmm7 has the sum now 
	
	;# add earlier value from mem 
	mov   eax, [ebp + mci1130_Vnb]
	addsd xmm7, [eax + edx*8] 
	;# move back to mem 
	movsd [eax + edx*8], xmm7 
	
	;# finish if last 
	mov   ecx, [ebp + mci1130_nri]
	dec ecx
	jecxz mci1130_end
	;# not last, iterate once more!  
	mov [ebp + mci1130_nri], ecx
	jmp mci1130_outer
mci1130_end:
	emms
	mov eax, [esp + mci1130_salign]
	add esp, eax
	add esp, 740
	pop edi
	pop esi
    pop edx
    pop ecx
    pop ebx
    pop eax
	leave
	ret



