	mov   edx, [ebp + mci3000_gid]      ;# get group index for this i particle 
	mov   edx, [edx]
	add dword ptr [ebp + mci3000_gid],  4  ;# advance pointer 

	;# accumulate total potential energy and update it 
	movapd xmm7, [esp + mci3000_vctot]
